import { MiniMetadata } from "@talismn/balances"
import { Chain, EvmNetwork, Token } from "@talismn/chaindata-provider"

// The init files imported in this module are generated by a script.
// Update them by running the following command:
// yarn workspace @talismn/chaindata-provider-extension generate-init-data

export const fetchInitChains = async (): Promise<Chain[]> => (await import("./chains")).chains
export const fetchInitEvmNetworks = async (): Promise<EvmNetwork[]> =>
  (await import("./evm-networks")).evmNetworks
export const fetchInitTokens = async (): Promise<Token[]> =>
  (await import("./tokens")).tokens as Token[]
export const fetchInitMiniMetadatas = async (): Promise<MiniMetadata[]> =>
  (await import("./mini-metadatas")).miniMetadatas as MiniMetadata[]
